import unittest
import big_input_1

from count_substrings import Solution

class TestSolution(unittest.TestCase):





    def test_001(self):
        s = "101101"
        sol = Solution()
        output = sol.numberOfSubstrings(s)
        self.assertEqual(output, 16) 

    def test_002(self):
        s = "00011" 
        sol = Solution()
        output = sol.numberOfSubstrings(s)
        self.assertEqual(output, 5)

    def test_003(self):
        s = ""
        sol = Solution()
        output = sol.numberOfSubstrings(s)
        self.assertEqual(output, 10440109)

    def test_004(self):
        s = big_input_1.s
        sol = Solution()
        output = sol.numberOfSubstrings(s)
        self.assertEqual(output, 799887988)

    def test_005(self):
        s = "1"
        sol = Solution()
        output = sol.numberOfSubstrings(s)
        self.assertEqual(output, 1)







try:
    unittest.main()
except SystemExit:
    None
















