import unittest

from count_substrings import Solution

class TestSolution(unittest.TestCase):

    def test_000(self):
        s = ""
        sol = Solution()
        output = sol.numberOfSubstrings(s)
        self.assertEqual(output, None)


    def test_001(self):
        s = "00011"
        sol = Solution()
        output = sol.numberOfSubstrings(s)
        self.assertEqual(output, 5)

    def test_002(self):
        s = "101101"
        sol = Solution()
        output = sol.numberOfSubstrings(s)
        self.assertEqual(output, 16) 







try:
    unittest.main()
except SystemExit:
    None
















