import unittest
from edit_distance import Solution




class TestSolution(unittest.TestCase):
    def test_0(self):
        s = Solution()
        result = s.minDistance(word1 = "horse", word2 = "ros")
        self.assertEqual(3, result)

    def test_1(self):
        s = Solution()
        result = s.minDistance(word1 = "intention", word2 = "execution")
        self.assertEqual(5, result)

    def test_2(self):
        s = Solution()
        word1 = ""
        word2 = ""
        result = s.minDistance(word1, word2)
        self.assertEqual(2, result)

try:
    unittest.main()
except SystemExit:
    None

